    {
        switch ($segment) {
            case 'form':
                return $this->getFormUniqueClasses();
            case 'reviewItem':
                return $this->getReviewItemUniqueClasses();
            case 'graph':
                return $this->getGraphUniqueClasses();
            case 'filter':
                return $this->getFilterUniqueClasses();
            default:
                return [];
        }
    }
